void insere() {
    int num;

    printf("digite um numero n");
    scanf("%d", &amp;num);

    Elemento *aux = (Elemento*) malloc(sizeof (Elemento));

    aux -&gt; dados = num;
    aux-&gt; proximo = NULL;

    if (primeiro == NULL) {
        primeiro = aux;
        ultimo = aux;

    } else {
        ultimo-&gt;proximo = aux;
        ultimo = aux;
    }
}

void removeLista() {
    if (primeiro == NULL)
        printf("lista vazia"); //return;
    else {
        Elemento * aux = primeiro;
        primeiro = primeiro-&gt;proximo;
        free(aux);

    }

}

